import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-root',
  standalone: true,
  template: `
    <div class="background" [style]="getBackgroundStyle()"></div>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
      height: 100vh;
      margin: 0;
      padding: 0;
      overflow: hidden;
    }

    .background {
      width: 100%;
      height: 100%;
      background: radial-gradient(circle at var(--mouse-x, 50%) var(--mouse-y, 50%), rgba(0, 102, 204, 0.3), transparent 50%);
      background-color: #0a0e27;
      transition: background 0.1s ease-out;
    }
  `]
})
export class AppComponent {
  title = 'IHK Trainer';
  mouseX = 50;
  mouseY = 50;

  @HostListener('document:mousemove', ['$event'])
  onMouseMove(event: MouseEvent) {
    const x = (event.clientX / window.innerWidth) * 100;
    const y = (event.clientY / window.innerHeight) * 100;
    this.mouseX = x;
    this.mouseY = y;
  }

  getBackgroundStyle() {
    return {
      '--mouse-x': `${this.mouseX}%`,
      '--mouse-y': `${this.mouseY}%`
    } as any;
  }
}
