import { Component, OnInit, OnDestroy, ViewChild, ElementRef, HostListener } from '@angular/core';

@Component({
  selector: 'app-root',
  standalone: true,
  template: `
    <canvas #waterCanvas class="water-canvas"></canvas>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
      height: 100vh;
      overflow: hidden;
    }

    .water-canvas {
      display: block;
      width: 100%;
      height: 100%;
    }
  `]
})
export class AppComponent implements OnInit, OnDestroy {
  @ViewChild('waterCanvas') canvasRef!: ElementRef<HTMLCanvasElement>;

  private canvas!: HTMLCanvasElement;
  private ctx!: CanvasRenderingContext2D;
  private width = 0;
  private height = 0;
  private animationId: number | null = null;

  // Water simulation
  private gridSize = 4;
  private grid: number[] = [];
  private gridWidth = 0;
  private gridHeight = 0;
  private damping = 0.99;
  private tension = 0.15;

  ngOnInit() {
    this.initCanvas();
    this.initWaterGrid();
    this.animate();
  }

  private initCanvas() {
    this.canvas = this.canvasRef.nativeElement;
    const ctx = this.canvas.getContext('2d');
    if (!ctx) return;

    this.ctx = ctx;
    this.width = window.innerWidth;
    this.height = window.innerHeight;

    this.canvas.width = this.width;
    this.canvas.height = this.height;
  }

  private initWaterGrid() {
    this.gridWidth = Math.ceil(this.width / this.gridSize) + 1;
    this.gridHeight = Math.ceil(this.height / this.gridSize) + 1;
    this.grid = new Array(this.gridWidth * this.gridHeight).fill(0);
  }

  @HostListener('window:resize')
  onResize() {
    this.width = window.innerWidth;
    this.height = window.innerHeight;
    this.canvas.width = this.width;
    this.canvas.height = this.height;
    this.initWaterGrid();
  }

  @HostListener('mousemove', ['$event'])
  onMouseMove(event: MouseEvent) {
    this.disturbWater(event.clientX, event.clientY, 30);
  }

  @HostListener('click', ['$event'])
  onClick(event: MouseEvent) {
    this.disturbWater(event.clientX, event.clientY, 80);
  }

  private disturbWater(x: number, y: number, force: number) {
    const gridX = Math.floor(x / this.gridSize);
    const gridY = Math.floor(y / this.gridSize);
    const radius = Math.ceil(force / this.gridSize);

    for (let gy = gridY - radius; gy <= gridY + radius; gy++) {
      for (let gx = gridX - radius; gx <= gridX + radius; gx++) {
        if (gx >= 0 && gx < this.gridWidth && gy >= 0 && gy < this.gridHeight) {
          const dist = Math.sqrt((gx - gridX) ** 2 + (gy - gridY) ** 2);
          if (dist < radius) {
            const influence = (1 - dist / radius) * force;
            const idx = gy * this.gridWidth + gx;
            this.grid[idx] += influence;
          }
        }
      }
    }
  }

  private updateWater() {
    // Update water physics
    const newGrid = [...this.grid];

    for (let y = 1; y < this.gridHeight - 1; y++) {
      for (let x = 1; x < this.gridWidth - 1; x++) {
        const idx = y * this.gridWidth + x;
        const neighbors =
          (this.grid[(y - 1) * this.gridWidth + x] +
            this.grid[(y + 1) * this.gridWidth + x] +
            this.grid[y * this.gridWidth + (x - 1)] +
            this.grid[y * this.gridWidth + (x + 1)]) /
          4;

        newGrid[idx] += this.tension * (neighbors - this.grid[idx]);
        newGrid[idx] *= this.damping;
      }
    }

    this.grid = newGrid;
  }

  private drawWater() {
    const imageData = this.ctx.createImageData(this.width, this.height);
    const data = imageData.data;

    // Base gradient colors
    const baseColor = { r: 102, g: 126, b: 234 };
    const lightColor = { r: 150, g: 180, b: 255 };

    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        const gridX = Math.floor(x / this.gridSize);
        const gridY = Math.floor(y / this.gridSize);

        let height = 0;
        let count = 0;

        // Bilinear interpolation
        for (let gy = gridY; gy <= gridY + 1; gy++) {
          for (let gx = gridX; gx <= gridX + 1; gx++) {
            if (gx >= 0 && gx < this.gridWidth && gy >= 0 && gy < this.gridHeight) {
              const dist =
                Math.abs(x - gx * this.gridSize) + Math.abs(y - gy * this.gridSize);
              const weight = 1 / (dist + 1);
              height += this.grid[gy * this.gridWidth + gx] * weight;
              count += weight;
            }
          }
        }

        height = count > 0 ? height / count : 0;
        const heightNorm = Math.max(0, Math.min(1, (height + 50) / 100));

        // Blend colors based on height
        const r = Math.round(
          baseColor.r + (lightColor.r - baseColor.r) * heightNorm
        );
        const g = Math.round(
          baseColor.g + (lightColor.g - baseColor.g) * heightNorm
        );
        const b = Math.round(
          baseColor.b + (lightColor.b - baseColor.b) * heightNorm
        );

        const idx = (y * this.width + x) * 4;
        data[idx] = r;
        data[idx + 1] = g;
        data[idx + 2] = b;
        data[idx + 3] = 255;
      }
    }

    this.ctx.putImageData(imageData, 0, 0);
  }

  private animate = () => {
    this.updateWater();
    this.drawWater();
    this.animationId = requestAnimationFrame(this.animate);
  };

  ngOnDestroy() {
    if (this.animationId !== null) {
      cancelAnimationFrame(this.animationId);
    }
  }
}
