import { Component, ViewChild, ElementRef, OnInit, AfterViewInit, HostListener } from '@angular/core';

interface Ripple {
  x: number;
  y: number;
  radius: number;
  maxRadius: number;
  opacity: number;
}

@Component({
  selector: 'app-root',
  standalone: true,
  template: `
    <canvas #canvas class="water-canvas"></canvas>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
      height: 100vh;
      margin: 0;
      padding: 0;
      overflow: hidden;
    }

    .water-canvas {
      display: block;
      background: linear-gradient(135deg, #0a1a2e, #1a3a52);
      cursor: crosshair;
    }
  `]
})
export class AppComponent implements OnInit, AfterViewInit {
  @ViewChild('canvas') canvasRef!: ElementRef<HTMLCanvasElement>;

  title = 'IHK Trainer';
  private ctx!: CanvasRenderingContext2D;
  private ripples: Ripple[] = [];
  private animationFrameId: number | null = null;

  ngOnInit() {
    // Initialization if needed
  }

  ngAfterViewInit() {
    const canvas = this.canvasRef.nativeElement;
    this.ctx = canvas.getContext('2d')!;
    this.resizeCanvas();
    this.startAnimation();
  }

  private resizeCanvas() {
    const canvas = this.canvasRef.nativeElement;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
  }

  @HostListener('window:resize')
  onResize() {
    this.resizeCanvas();
  }

  @HostListener('mousemove', ['$event'])
  onMouseMove(event: MouseEvent) {
    const ripple: Ripple = {
      x: event.clientX,
      y: event.clientY,
      radius: 0,
      maxRadius: 150,
      opacity: 1
    };
    this.ripples.push(ripple);
  }

  private startAnimation() {
    const animate = () => {
      this.draw();
      this.animationFrameId = requestAnimationFrame(animate);
    };
    animate();
  }

  private draw() {
    const canvas = this.canvasRef.nativeElement;

    // Clear canvas with gradient background
    const gradient = this.ctx.createLinearGradient(0, 0, canvas.width, canvas.height);
    gradient.addColorStop(0, '#0a1a2e');
    gradient.addColorStop(1, '#1a3a52');
    this.ctx.fillStyle = gradient;
    this.ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Update and draw ripples
    this.ripples = this.ripples.filter(ripple => {
      ripple.radius += 3;
      ripple.opacity = Math.max(0, 1 - (ripple.radius / ripple.maxRadius));

      if (ripple.opacity > 0) {
        this.drawWaterRipple(ripple);
        return true;
      }
      return false;
    });
  }

  private drawWaterRipple(ripple: Ripple) {
    this.ctx.save();

    // Draw ripple circle
    this.ctx.strokeStyle = `rgba(100, 200, 255, ${ripple.opacity * 0.8})`;
    this.ctx.lineWidth = 3;
    this.ctx.beginPath();
    this.ctx.arc(ripple.x, ripple.y, ripple.radius, 0, Math.PI * 2);
    this.ctx.stroke();

    // Draw inner glow
    this.ctx.strokeStyle = `rgba(150, 220, 255, ${ripple.opacity * 0.4})`;
    this.ctx.lineWidth = 1;
    this.ctx.beginPath();
    this.ctx.arc(ripple.x, ripple.y, ripple.radius - 8, 0, Math.PI * 2);
    this.ctx.stroke();

    // Draw outer glow
    this.ctx.strokeStyle = `rgba(100, 180, 255, ${ripple.opacity * 0.3})`;
    this.ctx.lineWidth = 2;
    this.ctx.beginPath();
    this.ctx.arc(ripple.x, ripple.y, ripple.radius + 8, 0, Math.PI * 2);
    this.ctx.stroke();

    // Draw radial gradient glow at ripple center
    const radialGradient = this.ctx.createRadialGradient(
      ripple.x, ripple.y, ripple.radius - 20,
      ripple.x, ripple.y, ripple.radius + 20
    );
    radialGradient.addColorStop(0, `rgba(100, 200, 255, ${ripple.opacity * 0.3})`);
    radialGradient.addColorStop(1, `rgba(100, 200, 255, 0)`);

    this.ctx.fillStyle = radialGradient;
    this.ctx.beginPath();
    this.ctx.arc(ripple.x, ripple.y, ripple.radius + 20, 0, Math.PI * 2);
    this.ctx.fill();

    this.ctx.restore();
  }

  ngOnDestroy() {
    if (this.animationFrameId !== null) {
      cancelAnimationFrame(this.animationFrameId);
    }
  }
}
