import { Component, OnInit, ViewChild, ElementRef, HostListener, OnDestroy } from '@angular/core';

@Component({
  selector: 'app-root',
  standalone: true,
  template: `
    <canvas #waterCanvas class="water-canvas"></canvas>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
      height: 100vh;
      overflow: hidden;
      margin: 0;
      padding: 0;
    }

    .water-canvas {
      display: block;
      width: 100%;
      height: 100%;
    }
  `]
})
export class AppComponent implements OnInit, OnDestroy {
  @ViewChild('waterCanvas') canvasRef!: ElementRef<HTMLCanvasElement>;

  private canvas!: HTMLCanvasElement;
  private ctx!: CanvasRenderingContext2D;
  private width = 0;
  private height = 0;
  private animationId: number | null = null;

  private resolution = 8;
  private cols = 0;
  private rows = 0;

  private heightMap!: number[];
  private heightMapNext!: number[];
  private imageData!: ImageData;
  private data!: Uint8ClampedArray;

  private damping = 0.95;
  private waveSpeed = 0.15;
  private time = 0;

  ngOnInit() {
    this.initCanvas();
    this.initWaterSimulation();
    this.animate();
  }

  private initCanvas() {
    this.canvas = this.canvasRef.nativeElement;
    const ctx = this.canvas.getContext('2d', { willReadFrequently: true });
    if (!ctx) {
      console.error('Could not get canvas context');
      return;
    }

    this.ctx = ctx;
    this.width = window.innerWidth;
    this.height = window.innerHeight;

    this.canvas.width = this.width;
    this.canvas.height = this.height;
  }

  private initWaterSimulation() {
    this.cols = Math.ceil(this.width / this.resolution) + 1;
    this.rows = Math.ceil(this.height / this.resolution) + 1;

    const size = this.cols * this.rows;
    this.heightMap = new Array(size).fill(0);
    this.heightMapNext = new Array(size).fill(0);

    this.imageData = this.ctx.createImageData(this.width, this.height);
    this.data = this.imageData.data;

    // Initialize with waves
    for (let i = 0; i < size; i++) {
      this.heightMap[i] = Math.sin(i * 0.01) * 0.3;
    }
  }

  @HostListener('window:resize')
  onResize() {
    this.width = window.innerWidth;
    this.height = window.innerHeight;
    this.canvas.width = this.width;
    this.canvas.height = this.height;
    this.initWaterSimulation();
  }

  @HostListener('mousemove', ['$event'])
  onMouseMove(event: MouseEvent) {
    this.disturbWater(event.clientX, event.clientY, 15);
  }

  @HostListener('click', ['$event'])
  onClick(event: MouseEvent) {
    this.disturbWater(event.clientX, event.clientY, 30);
  }

  private disturbWater(x: number, y: number, radius: number) {
    const col = Math.floor(x / this.resolution);
    const row = Math.floor(y / this.resolution);
    const radiusSquared = radius * radius;

    for (let r = Math.max(0, row - radius); r < Math.min(this.rows, row + radius); r++) {
      for (let c = Math.max(0, col - radius); c < Math.min(this.cols, col + radius); c++) {
        const dx = c - col;
        const dy = r - row;
        const distSquared = dx * dx + dy * dy;

        if (distSquared < radiusSquared) {
          const idx = r * this.cols + c;
          const influence = Math.cos((Math.sqrt(distSquared) / radius) * Math.PI) * 0.5;
          this.heightMap[idx] += influence;
        }
      }
    }
  }

  private updateWater() {
    const c2 = this.waveSpeed * this.waveSpeed;

    for (let r = 1; r < this.rows - 1; r++) {
      for (let c = 1; c < this.cols - 1; c++) {
        const idx = r * this.cols + c;
        const top = (r - 1) * this.cols + c;
        const bottom = (r + 1) * this.cols + c;
        const left = r * this.cols + (c - 1);
        const right = r * this.cols + (c + 1);

        const h = this.heightMap[idx];
        const neighbors = (this.heightMap[left] + this.heightMap[right] + this.heightMap[top] + this.heightMap[bottom]) / 4;

        this.heightMapNext[idx] = neighbors * 2 - this.heightMapNext[idx] + (neighbors - h) * c2;
        this.heightMapNext[idx] *= this.damping;
      }
    }

    // Add some natural oscillation
    this.time += 0.01;
    for (let i = 0; i < this.cols; i++) {
      this.heightMapNext[i] += Math.sin(this.time + i * 0.1) * 0.02;
      this.heightMapNext[(this.rows - 1) * this.cols + i] += Math.sin(this.time + i * 0.1) * 0.02;
    }

    const temp = this.heightMap;
    this.heightMap = this.heightMapNext;
    this.heightMapNext = temp;
  }

  private renderWater() {
    const baseColor1 = { r: 102, g: 126, b: 234 };
    const baseColor2 = { r: 30, g: 30, b: 80 };

    for (let y = 0; y < this.height; y++) {
      for (let x = 0; x < this.width; x++) {
        const col = Math.floor(x / this.resolution);
        const row = Math.floor(y / this.resolution);

        let heightSample = 0;
        if (col >= 0 && col < this.cols && row >= 0 && row < this.rows) {
          heightSample = this.heightMap[row * this.cols + col];
        }

        // Exaggerate the height for more visible color changes
        const heightNormalized = Math.max(-1, Math.min(1, heightSample * 10));
        const factor = (heightNormalized + 1) / 2;

        const r = Math.round(baseColor2.r + (baseColor1.r - baseColor2.r) * factor);
        const g = Math.round(baseColor2.g + (baseColor1.g - baseColor2.g) * factor);
        const b = Math.round(baseColor2.b + (baseColor1.b - baseColor2.b) * factor);

        const idx = (y * this.width + x) * 4;
        this.data[idx] = r;
        this.data[idx + 1] = g;
        this.data[idx + 2] = b;
        this.data[idx + 3] = 255;
      }
    }

    this.ctx.putImageData(this.imageData, 0, 0);
  }

  private animate = () => {
    this.updateWater();
    this.renderWater();
    this.animationId = requestAnimationFrame(this.animate);
  };

  ngOnDestroy() {
    if (this.animationId !== null) {
      cancelAnimationFrame(this.animationId);
    }
  }
}
